package com.csdj.crmproject.crmproject.dao.Sales.PaymentPlan;import com.csdj.crmproject.crmproject.entity.ReCeiPlan;import com.csdj.crmproject.crmproject.entity.salesmanagement.Order;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import java.util.List;/** * @author 贺芳 * @description: 收款计划 */@Mapperpublic interface PaymentPlanMapper {    /**     * 查询收款总记录数     * @param rePlanCurrentState 收款计划状态     * @return记录数     */    public int planCount(@Param(value = "rePlanCurrentState") String rePlanCurrentState);    /**     * 查询收款计划     * @return list     */    public List<ReCeiPlan> findPlan(@Param(value = "rePlanCurrentState") String rePlanCurrentState);    /**     * 添加     * @param reCeiPlan     * @return     */    public int addPlan(ReCeiPlan reCeiPlan);    /**     * 根据收款计划编号查询收款信息     * @param pkRePlanId 收款编号     * @return ReCeiPlan     */    public ReCeiPlan findGetPlanId(@Param(value = "pkRePlanId") long pkRePlanId);    /**     * 修改订单     * @param reCeiPlan     * @return     */    public int updatePlan(ReCeiPlan reCeiPlan);    /**     * 批量删除收款计划     *     * @param array     * @return     */    public int deletePlan(int[] array);}