/* * 文 件 名： * 版权(c) 2018-xxx公司 xxx项目组： * 版 本 号： */package com.csdj.crmproject.crmproject.service.impl.Sales.PaymentPlan;import com.csdj.crmproject.crmproject.dao.Sales.PaymentPlan.PaymentPlanMapper;import com.csdj.crmproject.crmproject.entity.ReCeiPlan;import com.csdj.crmproject.crmproject.service.Sales.PaymentPlan.PaymentPlanServer;import com.github.pagehelper.PageHelper;import com.github.pagehelper.PageInfo;import org.springframework.stereotype.Service;import javax.annotation.Resource;import java.util.List;/** * @author 贺芳 * @description: 收款计划 */@Servicepublic class PaymentPlanServerImpl implements PaymentPlanServer {    @Resource    private PaymentPlanMapper paymentPlanMapper;    /**     * 收款总记录数     * @param rePlanCurrentState 收款状态     * @return     */    @Override    public int orderCount(String rePlanCurrentState) {        return paymentPlanMapper.planCount(rePlanCurrentState);    }    /**     * 查询及分页     * @param pageNum     * @param pageSize     * @return     */    @Override    public PageInfo<ReCeiPlan> findPlan(int pageNum, int pageSize,String rePlanCurrentState) {        PageHelper.startPage(pageNum, pageSize);        List<ReCeiPlan> list=paymentPlanMapper.findPlan(rePlanCurrentState);        PageInfo<ReCeiPlan> pageInfo=new PageInfo<>(list);        return pageInfo;    }    /**     * 添加     * @param reCeiPlan     * @return     */    @Override    public int addPlan(ReCeiPlan reCeiPlan) {        return paymentPlanMapper.addPlan(reCeiPlan);    }    /**     * 根据ID查询收款计划     * @param pkRePlanId 收款编号     * @return     */    @Override    public ReCeiPlan findGetPlanId(long pkRePlanId) {        return paymentPlanMapper.findGetPlanId(pkRePlanId);    }    /**     * 修改     * @param reCeiPlan     * @return     */    @Override    public int updatePlan(ReCeiPlan reCeiPlan) {        return paymentPlanMapper.updatePlan(reCeiPlan);    }    /**     * 删除     * @param array     * @return     */    @Override    public int deletePlan(int[] array) {        return paymentPlanMapper.deletePlan(array);    }}